import { useState } from 'react';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import styles from '../css/Home.module.css';

function Movie({ movie }) {
  const [styleShow, setStyleShow] = useState('');
  const onMouseEnter = () => setStyleShow(styles.show);
  const onMouseOut = () => setStyleShow('');

  return (
    <li className={styles.movie}>
      <Link
        // to={`/Movie/${movie.id}`}
        onMouseEnter={onMouseEnter}
        onMouseOut={onMouseOut}
      >
        <img src={movie.medium_cover_image} alt={`${movie.title} poster`} />
        <h2>{movie.title} ({movie.year})</h2>
      </Link>
      <b>&#10029; {movie.rating === 9 ? `${movie.rating}.0` : movie.rating}</b>
      <ul>
        {movie.genres.map((genre, index) => 
          <li key={index}>{genre}</li>  
        )}
      </ul>
      {/* <p>{movie.summary}</p> */}
      <div className={[`${styles.background} ${styleShow}`].join('')}>
        <img src={movie.medium_cover_image} alt={`${movie.title} poster`} />
      </div>
    </li>
  )
}

Movie.propTypes = {
  id: PropTypes.number.isRequired,
  medium_cover_image: PropTypes.string.isRequired,
  title: PropTypes.string.isRequired,
  genres: PropTypes.arrayOf(PropTypes.string).isRequired,
  summary: PropTypes.string.isRequired
};

export default Movie;
